// src/directives/reveal.js
const prefersReducedMotion = () =>
  window.matchMedia &&
  window.matchMedia("(prefers-reduced-motion: reduce)").matches;

export default {
  mounted(el, binding) {
    // opções: { delay: 0, y: 18, once: true }
    const opts = binding?.value || {};
    const delay = Number(opts.delay ?? 0);
    const y = Number(opts.y ?? 18);
    const once = opts.once !== false; // default true

    // Respeita acessibilidade
    if (prefersReducedMotion()) {
      el.style.opacity = "1";
      el.style.transform = "none";
      el.style.filter = "none";
      return;
    }

    // estado inicial (oculto)
    el.style.opacity = "0";
    el.style.transform = `translateY(${y}px) scale(0.98)`;
    el.style.filter = "blur(8px)";
    el.style.transition =
      "opacity 700ms ease, transform 700ms cubic-bezier(.2,.8,.2,1), filter 700ms ease";
    el.style.transitionDelay = `${delay}ms`;
    el.style.willChange = "opacity, transform, filter";

    const io = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (!entry.isIntersecting) return;

          // revela
          el.style.opacity = "1";
          el.style.transform = "translateY(0) scale(1)";
          el.style.filter = "blur(0)";

          if (once) io.unobserve(el);
        });
      },
      {
        threshold: 0.18,
        rootMargin: "0px 0px -10% 0px",
      }
    );

    io.observe(el);
    el.__revealIO = io;
  },
  unmounted(el) {
    if (el.__revealIO) el.__revealIO.disconnect();
    delete el.__revealIO;
  },
};
