// src/directives/reveal.js
const prefersReducedMotion = () =>
  window.matchMedia &&
  window.matchMedia("(prefers-reduced-motion: reduce)").matches;

export default {
  mounted(el, binding) {
    const opts = binding?.value || {};
    const delay = Number(opts.delay ?? 0);
    const y = Number(opts.y ?? 18);
    const once = opts.once !== false;

    if (prefersReducedMotion()) {
      el.classList.add("reveal-in");
      return;
    }

    el.style.setProperty("--reveal-delay", `${delay}ms`);
    el.style.setProperty("--reveal-y", `${y}px`);

    el.classList.add("reveal-init");

    const io = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (!entry.isIntersecting) return;
          el.classList.add("reveal-in");
          if (once) io.unobserve(el);
        });
      },
      { threshold: 0.18, rootMargin: "0px 0px -10% 0px" }
    );

    io.observe(el);
    el.__revealIO = io;

    // trigger immediately if element is already visible on load (useful for hero)
    const checkVisibility = () => {
      const rect = el.getBoundingClientRect();
      const isVisible = rect.top < (window.innerHeight * 0.9) && rect.bottom > 0;
      if (isVisible) {
        el.classList.add("reveal-in");
        if (once) io.unobserve(el);
      }
    };

    // check on next frame and also on window load (covers late-rendered content)
    requestAnimationFrame(checkVisibility);
    window.addEventListener("load", checkVisibility, { once: true });
    el.__revealCheck = checkVisibility;
  },
  unmounted(el) {
    if (el.__revealIO) el.__revealIO.disconnect();
    delete el.__revealIO;
    if (el.__revealCheck) {
      window.removeEventListener("load", el.__revealCheck);
      delete el.__revealCheck;
    }
  },
};
